struct netflow
{
	static const int _n = _<<2 , _e = 9999999;

	int to[_e],ne[_e],ev[_e],he[_n],ecnt;

	inline void adde(int a,int b,int c){to[++ecnt]=b,ev[ecnt]=c,ne[ecnt]=he[a],he[a]=ecnt;}
	inline void addde(int a,int b,int c){adde(a,b,c),adde(b,a,0);}

	int dis[_n],gap[_n],dcnt,S,T;

	inline int dfs(int x,int f)
	{
		if(!x || !f || x==T)return f;
		int r=f,mid=dcnt-1;
		for(int i=he[x];i;i=ne[i])
		{
			if(ev[i]<=0)continue;
			int b=to[i];
			if(dis[b]==dis[x]+1)
			{
				int g=dfs(b,min(r,ev[i]));
				r-=g,ev[i]-=g,ev[i^1]+=g;
				if(dis[T]>=dcnt)return f-r;
				if(!r)break;
			}
			mid=min(mid,dis[b]);
		}

		if(f==r)
		{
			if(--gap[dis[x]]==0)dis[T]=dcnt;
			dis[x]=mid+1,gap[dis[x]]++;
		}

		return f-r;
	}

	inline int run(int s,int t)
	{
		memset(dis,0,sizeof(dis));
		memset(gap,0,sizeof(gap));
		gap[0]=dcnt;
		int sflw=0;
		S=s,T=t;
		while(dis[T]<dcnt)sflw+=dfs(S,inf);
		return sflw;
	}

	netflow()
	{
		memset(to,0,sizeof(to));
		memset(ne,0,sizeof(ne));
		memset(ev,0,sizeof(ev));
		memset(he,0,sizeof(he));
		ecnt=1,dcnt=0;
	}

}nf;

