const int max_n=205;
const int max_m=40005;
struct edge{
    int u,v,next;
}G[max_m*2];
int head[max_n],tot;
void init(int n){
    tot=0;
    for(int i=0;i<=n;i++)head[i]=-1;
}
int match[max_n];
bool used[max_n];
void add_edge(int u,int v){
    G[tot]={u,v,head[u]};
    head[u]=tot++;
}
bool dfs(int u){
    used[u]=true;
    for(int i=head[u];~i;i=G[i].next){
        int v=G[i].v,w=match[v];
        if(w<0||!used[w]&&dfs(w)){
            match[u]=v,match[v]=u;
            return true;
        }
    }
    return false;
}
int solve(int n,int m){
    int res=0;
    for(int i=0;i<=n+m;i++)match[i]=-1;
    for(int u=1;u<=n;u++){
        if(match[u]==-1){
            for(int i=0;i<=n+m;i++)used[i]=false;
            if(dfs(u))res++;
        }
    }
    return res;
}
