void sieve(int n){
    p[0]=p[1]=true;
    for(int i=2;i<=n;i++){
        if(!p[i])pri[++cnt]=i;
        for(int j=1;j<=cnt&&1ll*i*pri[j]<=n;j++){
            p[i*pri[j]]=true;
            if(i%pri[j]==0)break;
        }
    }
}
