{
	for(Edge *i=head[k];i!=NULL;i=i->next)
	{
		if(vis[i->v]==tim)
			continue;
		vis[i->v]=tim;
		if(!match[i->v]||Match(match[i->v]))
		{
			match[i->v]=k;
			return true;
		}
	}
	return false;
}
