    edges.pb(e);
    g[a].pb(edges.size() - 1);
    e.w = -e.w; e.c = 0; swap(e.from, e.to);
    edges.pb(e);
    g[b].pb(edges.size() - 1);
}
bool spfa(int s, int t){
    for (int i = 1; i < N; i++){
        d[i] = 1e9;
        f[i] = 0;
    }
    queue < int > q;
    q.push(s);
    d[s] = 0;
    f[s] = 1;
    while(!q.empty()){
        int v = q.front();
        q.pop();
        f[v] = 0;
        for (int i = 0; i < g[v].size(); i++){
            Edge e = edges[g[v][i]];
            if (e.f < e.c && d[e.to] > d[v] + e.w){
                p[e.to] = g[v][i];
                d[e.to] = d[v] + e.w;
                if (!f[e.to])
                    q.push(e.to);
                f[e.to] = 1;
            }
        }
    }
    return d[t] != 1e9;
}
int flow(int s, int t){
    int cost = 0;
    while(spfa(s, t)){
        int v = t;
        while(v != s && v != 0){
            auto k = p[v];
            edges[k].f++;
            edges[k ^ 1].f--;
            cost += edges[k].w;
            v = edges[k].from;
        }
    }
    return cost;
}
