namespace MF { //init before you use it. when you use double, be careful.

	struct edge {int to, cap, rev; };

	vector<edge> G[MAX_N];
	bool used[MAX_N];

	void init(int n) {
		rep(i, 0, n) G[i].clear();
	}

	int add_edge(int from, int to, int cap) {
		G[from].push_back((edge){to, cap, (int)G[to].size()});
		G[to].push_back((edge){from, 0, (int)G[from].size() - 1});
		return (int)G[from].size() - 1;
	}

	int dfs(int v, int t, int f, bool change = true) { //if you just want to dfs, change = false
		if(v == t) return f;
		used[v] = true;
		for(int i = 0; i < (int)G[v].size(); i++) {
			edge &e = G[v][i];
			if(!used[e.to] && e.cap > 0) {
				int d = dfs(e.to, t, min(f, e.cap), change);
				if(d > 0) {
					if(change) {
						e.cap -= d;
						G[e.to][e.rev].cap += d;
					}
					return d;
				}
			}
		}
		return 0;
	}

	ll get(int s, int t) {
		ll flow = 0;
		while(true) {
			memset(used, 0, sizeof(used));
			int f = dfs(s, t, inf);
			if(f == 0) return flow;
			flow += f;
		}
	}
}

int N, M;
int E, O;
int A[110], B[210];
int C[210], D[210];

bool prime(int v) {
	if(v <= 2) return false;
	for(int i = 2; i * i <= v; i++) {
		if(v % i == 0) return false;
	}
	return true;
}

void solve() {
	cin >> N;
	M = 0;
	rep(i, 0, N) cin >> A[i];
	rep(i, 0, N) {
		int t1 = (find(A, A + N, A[i] - 1) != A + N);
		int t2 = (find(A, A + N, A[i] + 1) != A + N);
		if(t1 == 0) B[M++] = A[i] - 1;
		if(t2 == 0) B[M++] = A[i];
	}
	M = unique(B, B + M) - B;

	int s = M, t = M + 1;
	MF::init(M + 2);

	rep(i, 0, M) {
		if(B[i] % 2 == 0) C[E++] = B[i];
		else D[O++] = B[i];
	}
	rep(i, 0, E) {
		rep(j, 0, O) {
			if(prime(abs(i - j))) {
				MF::add_edge(i, j + E, 1);
			}
		}
	}
	rep(i, 0, E) MF::add_edge(s, i, 1);
	rep(i, 0, O) MF::add_edge(i + E, t, 1);

	int res = MF::get(s, t);
	if((E - res) % 2 == 0) {
		cout << res + (E - res) + (O - res) << "\n";
	}
	else {
		cout << res + (E - res - 1) + (O - res - 1) + 3 << "\n";
	}
}

