bool dfs(int u){
	if(vis[u])return false;
	vis[u]=1;
	for(int i=1;i<=p2;++i)if(g[u][i]){
		if(!match[i]||dfs(match[i])){
			match[i]=u;
			return true;
		}
	}
	return false;
