int TryKuhn(int v)
{
    if (marked[v]) return 0;
    marked[v] = 1;
    for (auto u : graph[v])
    {
        if (paired[u] == -1 || TryKuhn(paired[u]))
        {
            paired[u] = v;
            return 1;
        }
    }
    return 0;
}

int G()
{
    int res = 0;
    fill(paired, paired + 400, -1);
    for (int i = 0; i < n; i++)
    {
        fill(marked, marked + 400, 0);
        if (TryKuhn(i))
            res++;
    }
    return res;
}

