int check(int n)
{
	if (n <= 2) return 0;
	int m = sqrt(n);
	for (int i = 2; i <= m; i++) if (n%i == 0) return 0;
	return 1;
}

int link[205], used[205];
int dfs(int u)
{
	for (int j = 0; j < g[u].size(); j++)
	{
		int v = g[u][j];
		if (!used[v])
		{
			used[v] = 1;
			if (link[v] == -1 || dfs(link[v]))
			{
