void add(int u, int v, int w) {
  ne[++top] = fi[u], fi[u] = top, to[top] = v, fl[top] = w;
  ne[++top] = fi[v], fi[v] = top, to[top] = u, fl[top] = 0;
  // printf("%d %d %d\n", u, v, w);
}

int Dinic() {
  int res = 0;
  while (BFS()) {
    for (int i = 1; i <= T; ++i) cur[i] = fi[i];
    res += DFS(S, INF);
  }
  return res;
}

bool BFS() {
  memset(deep, 0, sizeof(deep));
  q[head = tail = 1] = S;
  deep[S] = 1;
  while (head <= tail) {
    int f = q[head++];
    for (int i = fi[f]; i; i = ne[i]) {
      if (deep[to[i]] || !fl[i]) continue;
      deep[to[i]] = deep[f] + 1;
      q[++tail] = to[i];
    }
  }
  return deep[T];
}

int DFS(int t, int f) {
  if (t == T) return f;
  int sumflow = 0;
  for (int &i = cur[t]; i; i = ne[i]) {
    if (deep[to[i]] != deep[t] + 1 || !fl[i]) continue;
    int tflow = DFS(to[i], min(fl[i], f - sumflow));
    sumflow += tflow;
    fl[i] -= tflow, fl[i ^ 1] += tflow;
  }
  return sumflow;
}

