	memset(dis,127/3,sizeof(dis)),memset(vis,0,sizeof(vis));
	int inf=dis[0];dis[S]=0;
	que.push(S);
	while(!que.empty()){
		int xx=que.front();que.pop();
		vis[xx]=0;
		for(int i=head[xx];i!=-1;i=x[i].nex){
			if(x[i].w&&dis[x[i].v]>dis[xx]+1){
				dis[x[i].v]=dis[xx]+1;
				if(!vis[x[i].v]) vis[x[i].v]=1,que.push(x[i].v);
			}
		}
	}return dis[T]!=inf;
}
int dfs(int u,int flow){
	if(u==T) return flow;
	int used=0;
	for(int i=head[u];i!=-1;i=x[i].nex){
		if(x[i].w&&dis[x[i].v]==dis[u]+1){
			int slow=dfs(x[i].v,min(flow-used,x[i].w));used+=slow;
			x[i].w-=slow,x[i^1].w+=slow;
			if(used==flow) break;
		}
	}if(!used) dis[u]=-1;
	return used;
}
