	for (i = 0; i < Va.size(); i++) {
		int t = 0;
		if (Sx.count(Va[i])) t ^= 1;
		if (Sx.count(Va[i] + 1)) t ^= 1;
		if (!t) continue;
		if (Va[i] % 2) V1.push_back(Va[i]);
		else V2.push_back(Va[i]);
	}

	int src = V1.size() + V2.size();
	int snk = src + 1;
	for (i = 0; i < V1.size(); i++) epush(src, i, 1);
	for (i = 0; i < V2.size(); i++) epush(i + V1.size(), snk, 1);
	for (i = 0; i < V1.size(); i++) {
		for (j = 0; j < V2.size(); j++) {
			int t = abs(V1[i] - V2[j]);
			if (!pchk[t]) epush(i, j, 1);
		}
	}

	int f = 0;
	while (DFS(src, snk, f + 1, INF)) f++;

	int ans = f * 2;
	int t1 = (int)V1.size() - f, t2 = (int)V2.size() - f;
	while (t1 >= 2) {
		ans += 2;
		t1 -= 2;
	}
	while (t2 >= 2) {
		ans += 2;
		t2 -= 2;
	}
	if (t1 == 1) ans += 3;
	return !printf("%d\n", ans);
}

