#define REP(i,n) for(int i=0;i<(n);i++)

bool isp(int x){
  if(x<=1)return false;
  if(x==2)return true;
  if(x%2==0)return false;
  for(int y=3;y*y<=x;y+=2){
    if(x%y==0)return false;
  }
  return true;
}

int n;
int x[125];
int cap[252][252];
int main(void){
  scanf("%d",&n);
  REP(i,n)scanf("%d",x+i);
  map<int,int> M;
  REP(i,n){
    M[x[i]] ^= 1;
    M[x[i]+1] ^= 1;
  }
  vector<int> even,odd;
  for(auto P : M)if(P.second==1){
    (P.first%2==0 ? even : odd).push_back(P.first);
  }
  int ne = even.size();
  int no = odd.size();
  n = ne + no + 2;
  int s = ne+no;
  int t = ne+no+1;
  REP(i,ne)cap[s][i] = 1;
  REP(i,no)cap[ne+i][t] = 1;
  REP(i,ne)REP(j,no){
    int d = abs(even[i]-odd[j]);
    if(d<3)continue;
    if(isp(d)){
      cap[i][ne+j] = 1;
    }
  }
  int f = 0;
  while(true){
    vector<int> bef(n,-1);
    vector<bool> vstd(n,false);
    stack<int> S;
    S.push(s);
    vstd[s] = true;
    while(S.size()){
      int p = S.top(); S.pop();
      if(p==t)break;
      REP(i,n)if(cap[p][i] && !vstd[i]){
        vstd[i] = true;
        bef[i] = p;
        S.push(i);
      }
    }
    if(bef[t]==-1)break;
    int cur = t;
    while(true){
      int nxt = bef[cur];
      if(nxt==-1)break;
      cap[nxt][cur] = 0;
      cap[cur][nxt] = 1;
      cur = nxt;
    }
  }
  int ans = 0;
  ans += f;
  ans += (ne-f);
  ans += (no-f);
  if((ne-f)%2==1)ans++;
  printf("%d\n",ans);
}

