inline void A(int u,int v,int w,int c)
{
	edges.push_back((node){u,v,w,0,c});
	edges.push_back((node){v,u,0,0,-c});
	int pp=edges.size();
	mp[u].push_back(pp-2);
	mp[v].push_back(pp-1);
}

inline bool spfa()
{
	for(int i=0;i<=t;i++) d[i]=inf,inq[i]=0,a[i]=inf;
	que.push(s); d[s]=0;
	while(!que.empty())
	{
		int u=que.front(); que.pop(); inq[u]=0;
		for(int i=0;i<mp[u].size();i++)
		{
			node &v=edges[mp[u][i]];
			if(v.cap-v.flow>0&&d[v.to]>d[u]+v.cost)
			{
				d[v.to]=d[u]+v.cost;
				a[v.to]=min(a[u],v.cap-v.flow);
				p[v.to]=mp[u][i];
				if(inq[v.to]==0) que.push(v.to),inq[v.to]=1;
			}
		}
	}
	if(d[t]==inf) return false;
	flow+=a[t];
	cost+=a[t]*d[t];
	for(int i=t;i!=s;i=edges[p[i]].from)
	{
		edges[p[i]].flow+=a[t];
		edges[p[i]^1].flow-=a[t];
	}
	return true;
}

