using i128 = __int128_t;
ll get_factor(ll n) {
    if(n == 3 or n == 5) return 0;
    ll x, y;
    ll iter = 0;
    while(true) {
        ++iter;
        x = iter % n;
        y = (i128(iter) * i128(iter) + 1) % i128(n);
        while(1) {
            ll g = gcd(n, abs(x - y));
            if(g == n) break;
            if(g > 1) return g;
            x = (i128(x) * i128(x) + 1) % i128(n);
            y = (i128(y) * i128(y) + 1) % i128(n);
            y = (i128(y) * i128(y) + 1) % i128(n);
        }
    }
}
ll modpow(ll a, ll n, ll mod) {
    ll res = 1;
    while(n) {
        if(n & 1) res = i128(a) * i128(res) % i128(mod);
        n >>= 1, a = i128(a) * i128(a) % i128(mod);
    }
    return res;
}
bool miller_rabin_test(ll p, ll s, ll d, ll n) {
    ll x = modpow(p, d, n);
    if(x == 1) return true;
    ll y = n - 1;
    rep(i, s) {
        if(x == y) return true;
        x = i128(x) * i128(x) % i128(n);
    }
    return false;
}
bool miller_rabin(ll n) {
    if(n == 1) return false;
    ll d = n - 1, s = 0;
    while(~d & 1) d >>= 1, ++s;
    if(n < 4759123141LL) {
        for(auto p : {2, 7, 61}) {
            if(p >= n) break;
            if(!miller_rabin_test(p, s, d, n)) return false;
        }
    } else {
        for(auto p : {2, 325, 9375, 28178, 450775, 9780504, 1795265022}) {
            if(p >= n) break;
            if(!miller_rabin_test(p, s, d, n)) return false;
        }
    }
    return true;
}

map<ll, int> factorize(ll n) {
    map<ll, int> res;
    while(~n & 1) res[2]++, n >>= 1;
    queue<ll> que;
    que.emplace(n);
    while(!que.empty()) {
        ll now = que.front();
        que.pop();
        if(now == 1) continue;
        if(miller_rabin(now)) {
            res[now]++;
            continue;
        }
        ll g = get_factor(now);
        que.emplace(g);
        que.emplace(now / g);
    }
    return res;
}

bool isPrime(ll n) { return miller_rabin(n); }
} // namespace Factorize
using namespace Factorize;
struct HopcroftKarp {
    vector<vector<int>> graph;
    vector<int> dist, match;
    vector<bool> used, vv;

    HopcroftKarp(int n, int m) : graph(n), match(m, -1), used(n) {}

    void add_edge(int u, int v) { graph[u].push_back(v); }

    void bfs() {
        dist.assign(graph.size(), -1);
        queue<int> que;
        for(int i = 0; i < graph.size(); i++) {
            if(!used[i]) {
                que.emplace(i);
                dist[i] = 0;
            }
        }

        while(!que.empty()) {
            int a = que.front();
            que.pop();
            for(auto &b : graph[a]) {
                int c = match[b];
                if(c >= 0 && dist[c] == -1) {
                    dist[c] = dist[a] + 1;
                    que.emplace(c);
                }
            }
        }
    }

    bool dfs(int a) {
        vv[a] = true;
        for(auto &b : graph[a]) {
            int c = match[b];
            if(c < 0 || (!vv[c] && dist[c] == dist[a] + 1 && dfs(c))) {
                match[b] = a;
                used[a] = true;
                return (true);
            }
        }
        return (false);
    }

    int bipartite_matching() {
        int ret = 0;
        while(true) {
            bfs();
            vv.assign(graph.size(), false);
            int flow = 0;
            for(int i = 0; i < graph.size(); i++) {
                if(!used[i] && dfs(i)) ++flow;
            }
            if(flow == 0) return (ret);
            ret += flow;
        }
    }

    void output() {
        for(int i = 0; i < match.size(); i++) {
            if(~match[i]) { cout << match[i] << "-" << i << endl; }
        }
    }
};

main() {
    INT(n);
    VEC(int, a, n);
    const int N = 1e7 + 2;
    vi p(N);
    rep(i, n) p[a[i]]++;
    rep(i, N - 1) { p[i] = p[i + 1] ^ p[i]; }
    vi x[2];
    rep(i, N) {
        if(p[i]) x[i & 1].eb(i);
    }
    HopcroftKarp g(x[0].size(), x[1].size());
    rep(i, x[0].size()) {
        rep(j, x[1].size()) {
            int d = abs(x[1][j] - x[0][i]);
            if(isPrime(d)) g.add_edge(i, j);
        }
    }
    int d = g.bipartite_matching();
    int ans = x[0].size() + x[1].size() - d;
    if((x[0].size() - d) & 1) ans++;
    cout << ans << endl;
}

