			npr[i*prm[j]]=1;
			if(i%prm[j]==0) break;
		}
	}
}

inline bool ispr(int x){
	if(x<3200) return !npr[x];
	int sq=sqrt(x)+1;
	for(int i=1;prm[i]<=sq;++i){
		if(x%prm[i]==0) return 0;
	}
	return 1;
}

namespace F{
	int dep[N<<1],que[N<<1],cur[N<<1];

	inline bool bfs(){
		memset(dep,0,sizeof(dep));
		int hd=0,tl=1;
		dep[0]=1;
		while(hd!=tl){
			int x=que[hd++];
			for(int i=cur[x]=fir[x];i;i=eg[i].nx){
				int y=eg[i].to;
				if(eg[i].wi&&!dep[y]){
					dep[y]=dep[x]+1;
					que[tl++]=y;
				}
			}
		}
		return dep[(N<<1)-1];
	}

	int dfs(int x,int t){
		if(x==(N<<1)-1) return t;
		int tp,tt=t;
		for(int &i=cur[x];i;i=eg[i].nx){
			int y=eg[i].to,v=eg[i].wi;
			if(v&&tt&&dep[x]+1==dep[y]&&(tp=dfs(y,std::min(v,tt)))){
				eg[i].wi-=tp;
				eg[i^1].wi+=tp;
				tt-=tp;
				if(!tt) break;
			}
		}
		return t-tt;
	}
}

