		int u=Q.front();Q.pop();
		for(int i=fi[u];i;i=ne[i])
		if(c[i]&&dis[to[i]]==inf){
			int v=to[i];
			dis[v]=dis[u]+1;
			if(v==T)return 1; Q.push(v);
		}
	}
	return dis[T]<=V;
}
int dfs(int u,int T,int flow){
	if(u==T)return flow;
	int usd=0;
	for(int i=cur[u];i;i=cur[u]=ne[i])
	if(c[i]&&dis[to[i]]==dis[u]+1){
		int fl=dfs(to[i],T,min(flow-usd,c[i]));
		if(!fl)continue;c[i]-=fl,c[i^1]+=fl,usd+=fl;
		if(usd>=flow)return usd;
	}
	return usd;
}
int dinic(int S,int T){
	int ans=0;
	while(bfs(S,T))ans+=dfs(S,T,inf);
	return ans;	
}
void add(int x,int y,int z){
	ne[++E]=fi[x],fi[x]=E,to[E]=y,c[E]=z;
	ne[++E]=fi[y],fi[y]=E,to[E]=x,c[E]=0;
}
