	pr[1]=1;
	for(int i=2;i<=n;i++){
		if(!pr[i]){
			prime[++sum]=i;
			if(i==2)pr[i]=1;
		}
		for(int j=1;j<=sum;j++){
			if(prime[j]*i>n)break;
			pr[prime[j]*i]=1;if(i%prime[j]==0)break;
		}
	}
}
