const int MAXX=1e7+5,MAXN=105;
int n,cnt;
int x_id[MAXN],y_id[MAXN],G[MAXN][MAXN],lx[MAXN],ly[MAXN],slack[MAXN],match[MAXN];
bool up[MAXX],vis_x[MAXN],vis_y[MAXN];

inline bool is_odd_prm(int x){
	for(int i=3;i*i<=x;i+=2){
		if(x%i==0) return false;
	}
	return true;
}

inline bool find_path(int x){
	vis_x[x]=true;
	for(int y=1,dta;y<=cnt;++y){
		if(vis_y[y] || G[x][y]==0x7f) continue;
		dta=lx[x]-ly[y]-G[x][y];
		if(dta==0){
			vis_y[y]=true;
			if(!match[y] || find_path(match[y])){
				match[y]=x;
				return true;
			}
		}
		else slack[y]=min(slack[y],dta);
	}
	return false;
}

inline int KM(){
	for(int x=1;x<=cnt;++x){
		lx[x]=INT_MIN;
		for(int y=1;y<=cnt;++y){
			if(G[x][y]<0x7f)
				lx[x]=max(lx[x],G[x][y]);
		}
	}
	for(int nowx=1,dta;nowx<=cnt;++nowx){
		memset(slack,0x7f,sizeof(slack));
		while(true){
			memset(vis_x,false,sizeof(vis_x));
			memset(vis_y,false,sizeof(vis_y));
			if(find_path(nowx)) break;
			else{
				dta=INT_MAX;
				for(int y=1;y<=cnt;++y){
					if(!vis_y[y]) dta=min(dta,slack[y]);
				}
				for(int tmpx=1;tmpx<=cnt;++tmpx){
					if(vis_x[tmpx]) lx[tmpx]-=dta;
				}
				for(int y=1;y<=cnt;++y){
					if(vis_y[y]) ly[y]+=dta;
					else slack[y]-=dta;
				}
			}
		}
	}
	int ret=0;
	for(int y=1;y<=cnt;++y){
		if(match[y]) ret+=G[match[y]][y];
	}
	return ret;
}

