		if(up[i]!=up[i-1])
			++cnt&1 ? x_id[(cnt+1)>>1]=i:y_id[(cnt+1)>>1]=i;
	}
	cnt>>=1;
	memset(G,0x7f,sizeof(G));
	for(int i=1;i<=cnt;++i){
		for(int j=1,dta;j<=cnt;++j){
			dta=abs(x_id[i]-y_id[j]);
			if((dta&1)==0) G[i][j]=2;
			else if(is_odd_prm(dta)) G[i][j]=1;
			else G[i][j]=3;
		}
	}
	printf("%d",KM());
}

