in_ ll dP(ll a, ll b, ll p)
{
    st_ ll r;
    for(r=1; b; a=a*a%p, b>>=1)
        b&1? r=r*a%p: 0;
    re_ r;
}

in_ char chk(ll a, ll p)
{
    st_ ll s, r, i;
    for(r=p-1, s=0; ~r&1; ++s, r/=2);
    if(i=dP(a, r, p), i==1 || i==p-1) re_ 1;
    for(;s--;)
        if((i=i*i%p)==p-1) re_ 1;
    re_ 0;
}

in_ char isP(int p)
{
    st_  int i;
    inc(0, i, 32)
        if(!chk(rand()%(p-2)+1, p))
            re_ 0;
    re_ 1;
}

