inline void sieve(int n = 1e7){
	_v[1]=1;
	for(int i=2;i<=n;i++){
		if(!_v[i])prime[++pcnt]=i;
		for(int j=1;j<=pcnt&&1ll*i*prime[j]<=n;j++){
			_v[i*prime[j]]=1;
			if(i%prime[j]==0)break;
		}
	}
}
