inline bool dfs(int x){
	for(size_t i=0;i<g[x].size();i++){
		int v=g[x][i];
		if(!vis[v]){
			vis[v]=1;
			if(!match[v]||dfs(match[v])){
				match[v]=i;
				return 1;
			}
		}
	}
	return 0;
}

