template<typename A,typename B> inline A fexp(A x,B p,A mo){A ans=1;for(;p;p>>=1,x=1LL*x*x%mo)if(p&1)ans=1LL*ans*x%mo;return ans;}

int n,m,Odd;

int A[maxn],B[maxn];

struct Edge{
	int to,cap;
	Edge *next,*oppo;
};
Edge *E[maxn],Pr[maxn*maxn<<2];int cnt=0;
void addedge(int u,int v,int cap){
	Pr[++cnt].to=v;Pr[cnt].cap=cap;Pr[cnt].oppo=&Pr[cnt+1];Pr[cnt].next=E[u];E[u]=&Pr[cnt];
	Pr[++cnt].to=u;Pr[cnt].cap=0;  Pr[cnt].oppo=&Pr[cnt-1];Pr[cnt].next=E[v];E[v]=&Pr[cnt];
}

int s,t;
int H[maxn];

bool Makelevel(){
	memset(H,127,sizeof(H));H[s]=0;
	static queue<int> Q;Q.push(s);
	while(!Q.empty()){int u=Q.front();Q.pop();
		for(Edge *P=E[u];P;P=P->next)
			if(P->cap&&H[P->to]>H[u]+1){
				H[P->to]=H[u]+1;Q.push(P->to);
			}
	}
	return H[t]!=MAX;
}

int extend(int u,int left){int r=0;
	if(u==t)return left;
	for(Edge *P=E[u];P&&r<left;P=P->next)
		if(P->cap&&H[P->to]==H[u]+1){int t;
			t=extend(P->to,min(P->cap,left-r));
			r+=t;P->cap-=t;P->oppo->cap+=t;
		}
	if(!r)H[u]=MAX;
	return r;
}

bool check(int n){
	if(n<=2)return false;
	for(int i=2;1LL*i*i<=n;i++)if(n%i==0)return false;
	return true;
}

void Work(){
	int ans=0;
	while(Makelevel())ans+=extend(s,MAX);
	ans+=(Odd-ans)/2*2+(n-Odd-ans)/2*2+((Odd-ans)%2==1?3:0);
	iout(ans);
}

void Init(){
	read(n);
	REP(i,1,n)read(A[i]);

	sort(A+1,A+n+1);A[0]=-1;
	REP(i,1,n){
		if(A[i]!=A[i-1]+1)B[++m]=A[i];
		if(A[i+1]!=A[i]+1)B[++m]=A[i]+1;
	}

	n=m;memcpy(A,B,sizeof(A));

	s=0;t=n+1;
	REP(i,1,n)if(A[i]&1)Odd++,addedge(s,i,1);else addedge(i,t,1);
	REP(i,1,n)if(A[i]&1)
		REP(j,1,n)if(A[j]&1);else if(check(abs(A[i]-A[j])))addedge(i,j,1);
}

int main(){
	Init();
	Work();
	return 0;
}

