        for (int j = i + 1; j < c.size(); ++j) {
            cost[i][j] = cost[j][i] = get(c[i], c[j]);
        }   
    }    

    for (int mask = 1; mask < (1 << c.size()); ++mask) {
        dp[mask] = INF;
        int l = -1;
        for (int i = 0; i < c.size(); ++i)
            if ((mask >> i) & 1) {
                l = i;
                break;
            }
        for (int i = l + 1; i < c.size(); ++i) {
            if ((mask >> i) & 1) {
                dp[mask] = min(dp[mask], dp[mask - (1 << l) - (1 << i)] + cost[i][l]);
            }   
        }   
    }
    cout << dp[(1 << c.size()) - 1] << endl;
}

