class BipartiteMatching {
  bool dfs(int v) {
    used[v] = true;
    for (int i = 0; i < (int)G[v].size(); i++) {
      int u = G[v][i], w = match[u];
      if (w < 0 || (!used[w] && dfs(w))) {
        match[v] = u;
        match[u] = v;
        return true;
      }
    }
    return false;
  }

  public:
  int V;
  vector<vector<int>> G;
  vector<int> match;
  vector<bool> used;

  BipartiteMatching(int V) : V(V), G(V), match(V, -1), used(V) {}

  void add_edge(int u, int v) {
    G[u].push_back(v);
    G[v].push_back(u);
  }

  int matching() {
    int res = 0;
    for (int v = 0; v < V; v++) {
      if (match[v] < 0) {
        fill(used.begin(), used.end(), 0);
        if (dfs(v)) {
          res++;
        }
      }
    }
    return res;
  }
};

