    }
    if (i + 1 < n) {
      if (A[i+1] != A[i] + 1) B.emplace_back(A[i]+1);
    }
    else {
      B.emplace_back(A[i]+1);
    }
  }

  out(A);
  out(B);

  vi E, O;
  forr(b, B) (b % 2 ? O : E).emplace_back(b);

  int e = sz(E);
  int o = sz(O);

  BipartiteMatching bm(e + o);

  rep(i, e) rep(j, o) {
    int d = abs(E[i] - O[j]);
    if (is_prime(d)) {
      bm.add_edge(i, e + j);
    }
  }

  int k = bm.matching();

  int k2_e = (e - k) / 2;
  int k2_o = (o - k) / 2;

  int k3 = (e - k) % 2;

  int ans = k + k2_e * 2 + k2_o * 2 + k3 * 3;

  cout << ans << endl;
}

