        if (dp[l][k] == INF || dp[k+1][r] == INF) continue;
        dp[l][r] = min(dp[l][r], dp[l][k] + dp[k+1][r]);
      }
      // ( x )
      int s = INF;
      if (len == 1) s = 0;
      else s = dp[l+1][r-1];
      dp[l][r] = s+cost[l][r];
    }
