        int temp; cin >> temp;
        if(temp & 1)
        {
            if(od.size() > 0 and od.back() == temp) od.pop_back(); else od.pb(temp);
            ev.pb(temp+1);
        }
        else
        {
            if(ev.size() > 0 and ev.back() == temp) ev.pop_back(); else ev.pb(temp);
            od.pb(temp+1);
        }
    }

    Turbo turbo(ev.size(), od.size());
    for(int a = 0; a < ev.size(); ++a)
        for(int b = 0; b < od.size(); ++b)
        {
            if(prime(abs(od[b]-ev[a]))) turbo.addEdge(a,b);
        }
    int k = turbo.bestMatching();
    int ans = k + ((ev.size()-k)/2+(od.size()-k)/2) * 2 + ((ev.size()-k) % 2) * 3;
    cout << ans << endl;
}

