const int N=205,L=1e7+5;
bool mark[L],prime[L];
namespace match
{
  const int N=205;
  int l[N],r[N],n,m;
  bool a[N][N],seen[N];
  bool findmatch(int i)
  {
    rep(j,0,m)
      {
	if(a[i][j] && !seen[j])
	  {
	    seen[j]=true;
	    if(r[j]<0 || findmatch(r[j]))
	      {
		l[i]=j; r[j]=i;
		return true;
	      }
	  }
      }
    return false;
  }
  int bipartitematch()
  {
    int cnt=0;
    rep(i,0,n) l[i]=-1;
    rep(i,0,m) r[i]=-1;
    rep(i,0,n)
      {
	rep(j,0,m) seen[j]=false;
	if(findmatch(i)) cnt++;
      }
    return cnt;
  }
};
int main()
{
  std::ios::sync_with_stdio(false);
  cin.tie(NULL) ; cout.tie(NULL) ;
  memset(prime,true,sizeof(prime));
  prime[1]=false;
  rep(i,2,L)
    {
      if(!prime[i]) continue;
      for(int j=2*i;j<L;j+=i)
	prime[j]=false;
    }
  prime[2]=false;
  int n,x;
  cin>>n;
  rep(i,1,n+1)
    {
      cin>>x;
      mark[x]=true;
    }
  vi arr;
  rep(i,1,L-1)
    if((mark[i-1] && !mark[i])  || (!mark[i-1] && mark[i]))
      arr.pb(i);
  match::n=match::m=sz(arr);
  int odd=0,even=0;
  for(int x:arr)
    {
      if(x%2==0) even++;
      else odd++;
    }
  rep(i,0,sz(arr))
    rep(j,i+1,sz(arr))
    {
      if(prime[arr[j]-arr[i]]) match::a[i][j]=1;
      else match::a[i][j]=0;
    }
  int k=match::bipartitematch();
  int ans=k+((even-k)/2+(odd-k)/2)*2;
  if((even-odd)&1==0) ans+=3*((even-k)&1);
  else
    {
      ans+=((even-k)&1)*3;
      ans+=((odd-k)&1)*3;
    }
  cout<<ans<<endl;
  return 0 ;
}

