        if (!v.empty() && v.back() == x[i]) { v.pop_back(); }
        else { v.emplace_back(x[i]); }
        v.emplace_back(x[i] + 1);
    }
    vint even, odd;
    rep(i, v.size()) {
        if (v[i] & 1) { odd.emplace_back(v[i]); }
        else { even.emplace_back(v[i]); }
    }
    vvint adj(330), cap(330);
    dinic<int_dinic> dnc(adj, cap);
    rep(i, even.size()) { dnc.add_edge(0, 100 + i, 1); }
    rep(i, odd.size()) { dnc.add_edge(200 + i, 300, 1); }
    rep(i, even.size()) {
        rep(j, odd.size()) {
            if (get_divisors(abs(even[i] - odd[j])).size() == 2) {
                dnc.add_edge(100 + i, 200 + j, 1);
            }
        }
    }
    int f = dnc.calc_max_flow(0, 300);
    int ans = v.size() + f;
    if ((even.size() - f) & 1) { ans++; }
