void prp()
{
	fo(i,2,10000000)
	{
		if(!bz[i]) pr[++pr[0]]=i;
		for(int j=1;j<=pr[0]&&i*pr[j]<=10000000;j++)
		{
			bz[i*pr[j]]=1;
			if(i%pr[j]==0) break;
		}
	}
	bz[2]=0;
}
bool find(int k)
{
	fo(i,1,a1[k][0])
	{
		int p=a1[k][i];
		if(!bp[p])
		{
			bp[p]=1;
			if(!f[p]||find(f[p])) 
			{
				f[p]=k;
				return 1;
			}
		}
	}
	return 0;
}
