#include<bits/stdc++.h>
using namespace std;
#define MAXN	105
#define MAXP	405
#define MAXQ	1000005
#define MAXV	100000005
#define INF	1e9
template <typename T> void read(T &x) {
	x = 0; int f = 1;
	char c = getchar();
	for (; !isdigit(c); c = getchar()) if (c == '-') f = -f;
	for (; isdigit(c); c = getchar()) x = x * 10 + c - '0';
	x *= f;
}
struct edge {int dest, flow; unsigned home; };
vector <edge> a[MAXP];
unsigned curr[MAXP];
int s, t, dist[MAXP];
int odd, vodd[MAXN], even, veven[MAXN];
int tot, f[MAXV], prime[MAXV];
bool isprime[MAXV], mark[MAXV];
void addedge(int x, int y, int z) {
	a[x].push_back((edge) {y, z, a[y].size()});
	a[y].push_back((edge) {x, 0, a[x].size() - 1});
}
bool bfs() {
	static int q[MAXP];
	memset(dist, 0, sizeof(dist));
	int l = 0, r = 0;
	q[0] = s; dist[s] = 1;
	while (l <= r) {
		int now = q[l];
		for (unsigned i = 0; i < a[now].size(); i++)
			if (dist[a[now][i].dest] == 0 && a[now][i].flow != 0) {
				dist[a[now][i].dest] = dist[now] + 1;
				q[++r] = a[now][i].dest;
			}
		l++;
	}
	return dist[t] != 0;
}
int dinic(int pos, int limit) {
	if (pos == t) return limit;
	int used = 0, tmp;
	for (unsigned &i = curr[pos]; i < a[pos].size(); i++)
		if (dist[a[pos][i].dest] == dist[pos] + 1 && a[pos][i].flow && (tmp = dinic(a[pos][i].dest, min(limit - used, a[pos][i].flow)))) {
			used += tmp;
			a[pos][i].flow -= tmp;
			a[a[pos][i].dest][a[pos][i].home].flow += tmp;
			if (used == limit) return used;
		}
	return used;
}
int main() {
	int n; read(n);
	for (int i = 1; i <= n; i++) {
		int x; read(x);
		mark[x] ^= true;
		mark[x + 1] ^= true;
	}
	for (int i = 1; i < MAXV; i++)
		if (mark[i]) {
			if (i & 1) vodd[++odd] = i;
			else veven[++even] = i;
		}
	for (int i = 2; i < MAXV; i++) {
		if (f[i] == i) {
			f[i] = i;
			prime[++tot] = i;
			isprime[i] = true;
		}
		for (int j = 1; j <= tot && prime[j] <= f[i]; j++) {
			int tmp = prime[j] * i;
			if (tmp >= MAXV) break;
			f[tmp] = prime[j];
		}
	}
	s = 0, t = odd + even + 1;
	for (int i = 1; i <= odd; i++)
		addedge(s, i, 1);
	for (int i = 1; i <= even; i++)
		addedge(i + odd, t, 1);
	for (int i = 1; i <= odd; i++)
	for (int j = 1; j <= even; j++)
		if (isprime[abs(vodd[i] - veven[j])]) addedge(i, j + odd, 1);
	int ans = 0;
	while (bfs()) {
		memset(curr, 0, sizeof(curr));
		ans += dinic(s, INF);
	}
	cout << ans + (odd - ans) / 2 * 2 + (even - ans) / 2 * 2 + (odd - ans) % 2 * 3 << endl;
	return 0;
}

