int n;

struct Turbo
{
    int n, m;
    vector<int> lm, rm;
    vector<bool> visited;
    vector<vector<int>> graph;

    Turbo(int n, int m)
    {
        this->n = n;
        this->m = m;
        lm.resize(n,-1); rm.resize(m,-1);
        visited.resize(n,false);
        graph.resize(n);
    }

    void addEdge(int a, int b)
    {
        graph[a].pb(b);
    }

    int bestMatching()
    {
        int res = 0;
        while(1)
        {
            bool change = false;
            for(int i = 0; i < n; ++i) visited[i] = false;
            for(int i = 0; i < n; ++i)
            {
                if(lm[i] == -1 and findPath(i))
                {
                    res++;
                    change = true;
                }
            }
            if(!change) break;
        }
        return res;
    }

    bool findPath(int x)
    {
        if(visited[x]) return false;
        visited[x] = true;
        for(auto &i:graph[x])
        {
            if(rm[i] == -1 or findPath(rm[i]))
            {
                lm[x] = i;
                rm[i] = x;
                return true;
            }
        }
        return false;
    }

};

bool prime(int x)
{
    if(x < 3) return false;
    for(int i = 2; i*i <= x; ++i) if(x % i == 0) return false;
    return true;
}

