  m /= 2;
  vector<pii> ps;
  rep(i, m) {
    ps.eb(pii(vs[i*2], vs[i*2+1]));
  }

  while (1) {
    bool found = false;
    rep(i, m) {
      int a, b;
      tie(a, b) = ps[i];
      reps(j, i+1, m) {
        int c, d;
        tie(c, d) = ps[j];

        rep(k, 2) {
          int pr = GetCost(abs(a-b)) + GetCost(abs(c-d));
          int af = GetCost(abs(a-c)) + GetCost(abs(b-d));
          if (pr > af) {
            found = true;
            ps[i] = pii(a, c);
            ps[j] = pii(b, d);
            goto L_END;
          } else if (pr == af && af == 4 && GetCost(abs(a-c))%2 && GetCost(abs(a-b))%2 == 0) {
            found = true;
            ps[i] = pii(a, c);
            ps[j] = pii(b, d);
            goto L_END;
          }

          swap(c, d);
        }
      }
L_END:
      continue;
    }

    if (!found) break;
  }

  int ans = 0;
  rep(i, m) {
    ans += GetCost(abs(ps[i].X-ps[i].Y));
  }
  cout << ans << endl;
}

