	n=read();
	for(int i=1;i<=n;++i) b[i]=read();b[0]=-1;
	for(int i=1;i<=n;++i){
		if(b[i]!=b[i-1]+1){
			if(b[i]%2) odd[++odd_num]=b[i];
			else even[++even_num]=b[i];
		}
		if(b[i]+1!=b[i+1]){
			if((b[i]+1)%2) odd[++odd_num]=b[i]+1;
			else even[++even_num]=b[i]+1;
		}
	}
	for(int i=1;i<=odd_num;++i){
		for(int j=1;j<=even_num;++j){
			if(check(abs(even[j]-odd[i]))) add_edge(i,j+odd_num);
		}
	}
	for(int i=1;i<=odd_num;++i){
		tim++;
		if(find(i)) k++;
		else break;
	}
	ans=k+((even_num-k)/2+(odd_num-k)/2)*2+((even_num-k)%2)*3;
	printf("%d",ans);
	return 0;
}

#include<bits/stdc++.h>
using namespace std;
#define left lft

const int N=1e7+5;
const int maxn=N+10;

int n,m,m0,m1,c[210],k1,left[210];
bool vis[210];
bool comp[maxn]; //composite
int primes,prime[maxn/10];

void prework()
{
	int i,j;
	comp[0]=comp[1]=1;
	for (i=2;i<=N;i++)
	{
		if (!comp[i]) prime[++primes]=i;
		for (j=1;j<=primes&&i*prime[j]<=N;j++)
		{
			comp[i*prime[j]]=1;
			if (i%prime[j]==0) break;
		}
	}
	comp[2]=1;
}

bool match(int u)
{
	for (int v=1;v<=m;v++)
	{
		if (comp[abs(u-c[v])]||vis[v]) continue;
		vis[v]=1;
		if (!left[v]||match(left[v]))
		{
			left[v]=u;
			return 1;
		}
	}
	return 0;
}

int main()
{
#ifdef h10
	freopen("F.in","r",stdin);
	freopen("F.out","w",stdout);
#endif
	int i,x;
	prework();
	scanf("%d",&n);
	for (i=1;i<=n;i++)
	{
		scanf("%d",&x);
		c[(i<<1)-1]=x;
		c[i<<1]=x+1;
	}
	sort(c+1,c+n+n+1);
	for (i=1;i<=n+n;i++)
		c[i]==c[m]?m--:c[++m]=c[i];
	for (i=1;i<=m;i++)
		c[i]&1?m1++:m0++;
	for (i=1;i<=m;i++)
	{
		memset(vis,0,sizeof(vis));
		if (c[i]&1) k1+=match(i);
	}
	m0-=k1; m1-=k1;
	printf("%d\n",k1+((m0>>1)+(m1>>1))*2+(m0&1)*3);
}

