        {
            offset += get(s[j] - s[j - 1] - 1);
            smin(dp[i],offset + dp[j - 1] + get(s[i] - s[j - 1]));
            if (j >= 2)
                smin(dp[i],offset + dp[j - 2] + get(s[i] - s[j - 1] + 1));
        }
    }
    fo << dp[n] << '\n';
    cerr << "Time elapsed :" << clock() * 1000.0 / CLOCKS_PER_SEC << " ms" << '\n';
    return 0;
}

