	for (int i = 0; i < N; i++) {
		p.push_back(x[i]);
		for (; i < N - 1 && x[i] + 1 == x[i + 1]; i++);/*  */
		p.push_back(x[i] + 1);
	}
	int s = MAX_V - 2;
	int t = s + 1;
	int num = p.size();
	int evenNum = 0;
	for (int i = 0; i < num; i++) {
		if (0 == (p[i] % 2)) {
			add_edge(s, i, 1);
			evenNum++;
			for (int j = 0; j < num; j++) {
				int dst = abs(p[i] - p[j]);
				if (dst > 2 && is_prime(dst)) add_edge(i, j, 1);
			}
		} else {
			add_edge(i, t, 1);
		}
	}
	int f = max_flow(s, t);
	int ans = f;
	ans += ((evenNum - f)/2 + (num - evenNum - f)/2)*2 ;
	ans += ((evenNum - f) % 2) * 3;
	cout << ans << endl;
}

