int n,k,a[N],b[N],f[N],next[N],p[N],now[N],d[N],h[N],cnt,ans;
vector<int> x;
queue<int> q;

void add(int x,int y){
	a[++k]=x;b[k]=y;f[k]=1;next[k]=p[x];p[x]=k;
	a[++k]=y;b[k]=x;f[k]=0;next[k]=p[y];p[y]=k;
}

bool travel(){
	memcpy(now,p,sizeof(p));
	fill(d+1,d+n+3,inf);d[n+1]=0;
	q.push(n+1);h[n+1]=1;
	while (!q.empty()){
		int x=q.front();q.pop();h[x]=0;
		for (int t=p[x];t;t=next[t])
			if (f[t]&&d[x]+1<d[b[t]]){
				d[b[t]]=d[x]+1;
				if (!h[b[t]]) q.push(b[t]),h[b[t]]=1;
			}
	}
	return d[n+2]<inf;
}

int dinic(int x,int lmt){
	if (x==n+2) return lmt;int cnt=0;
	for (int t=now[x];t;t=now[x]=next[t])
		if (f[t]&&d[x]<d[b[t]]){
			int tmp=dinic(b[t],min(f[t],lmt-cnt));
			f[t]-=tmp;f[t^1]+=tmp;cnt+=tmp;
			if (cnt==lmt) return lmt;
		}
	return cnt;
}

