vi prime;

int add(int d) {
  if (d == 1) return 3;
  if (!(d & 1)) return 2;
  for (int &p : prime) {
    if (p * p > d) break;
    if (d % p == 0) return 3;
  }
  return 1;
}

void solve() {
  int n; cin >> n;
  vi arr(n); cin >> arr;

  vi sec = {arr[0], arr[0] + 1};
  rep1(i,n) {
    if (sec.back() == arr[i]) {
      ++sec.back();
    } else {
      sec.emplace_back(arr[i]);
      sec.emplace_back(arr[i] + 1);
    }
  }

  vb seen(3200, false);

  for (int i=3; i<3200; i+=2) {
    if (!seen[i]) {
      prime.emplace_back(i);
      for (int j=i*3; j<3200; j+=i*2) {
        seen[j] = true;
      }
    }
  }

  int sz = sec.size();

  Graph<int> dp(sz, vi(sz, 0));

  rep1(j,sz) {
    for (int i=j-1; i>=0; i-=2) {
      int d = sec[j] - sec[i];
      dp[i][j] = dp[i+1][j-1] + add(d);
      for (int k=i+1; k<j; k+=2) {
        chmin(dp[i][j], dp[i][k] + dp[k+1][j]);
      }
    }
  }

  cout << dp[0][sz-1] << endl;
}


