#define INF (1 << 29)
bool prime(int x){
  if(x == 1)return false;
  for(int i = 2;i * i <= x;i++){
    if(x % i == 0)return false;
  }
  return true;
}

class E{
public:
  int to, lim, inv;
  E(int a, int b, int c){
    to = a;
    lim = b;
    inv = c;
  }
};

vector<E> edge[300];
int n, x;
map<int, int> cnt;
vector<int> odd, even;

void add_edge(int u, int v, int f){
  edge[u].push_back(E(v, f, edge[v].size()));
  edge[v].push_back(E(u, 0, edge[u].size() - 1));
}


bool come[300];
int flow(int s, int t, int f){
  if(come[s])return 0;
  come[s] = true;
  if(s == t)return f;
  for(int i = 0;i < edge[s].size();i++){
    E &e = edge[s][i];
    if(e.lim == 0)continue;
    int tmp = flow(e.to, t, min(f, e.lim));
    if(tmp == 0)continue;
    e.lim -= tmp;
    edge[e.to][e.inv].lim += tmp;
    return tmp;
  }
  return 0;
}

int max_flow(int s, int t){
  int res = 0;
  while(true){
    for(int i = 0;i < 300;i++)come[i] = false;
    int tmp = flow(s, t, INF);
    if(tmp == 0)return res;
    res += tmp;
  }
}


