  for(int i = 0;i < n;i++){
    cin >> x;
    cnt[x]^=1;
    cnt[x-1]^=1;
  }
  map<int, int>::iterator it;
  for(it = cnt.begin();it != cnt.end();it++){
    if(it->second == 0)continue;
    x = it->first;
    if(x % 2 == 0)even.push_back(x);
    if(x % 2 == 1)odd.push_back(x);
  }
  int s = even.size() + odd.size() + 1;
  int t = s + 1;
  for(int i = 0;i < even.size();i++)add_edge(s, i, 1);
  for(int i = 0;i < odd.size();i++)add_edge(even.size() + i, t, 1);

  for(int i = 0;i < even.size();i++){
    for(int j = 0;j < odd.size();j++){
      int dif = abs(even[i] - odd[j]);
      if(prime(dif))add_edge(i, even.size() + j, 1);
    }
  }

  int mxf = max_flow(s, t);
  cout << mxf + (even.size() - mxf) / 2 * 2 + (odd.size() - mxf) / 2 * 2 + (even.size() - mxf) % 2 * 3 << endl;
  return 0;
}

