	char ch = gc(); LL ret = 0, sgn = 1;
	while(ch < '0' || ch >'9') {
		if(ch == '-') sgn = -1;
		ch = gc();
	}
	while(ch >= '0' && ch <= '9')
		ret = ret * 10 + ch - '0', ch = gc();
	return ret * sgn;
}

inline void out(LL x) {
	static int buf[50], btp;
	if(x < 0) x = -x, pc('-');
	if(!x) pc('0');
	else {
		btp = 0;
		for(; x; x /= 10) buf[++ btp] = x % 10;
		while(btp) pc('0' + buf[btp --]);
	}
}

