	edge[++ cnt] = (qwq) { y, head[x] }, head[x] = cnt;
}
int lft[N], rt[N], lsz, rsz;
const int M = 1e7 + 2;
bool isnotp[M + 1];
int tot, p[700000], vis[N], tim, opn[N], n, a[N];
inline void Sieve() {
	isnotp[1] = 1;
	REP(i, 2, M) {
		if(!isnotp[i]) p[++ tot] = i;
		REP(j, 1, tot) {
			if((LL)i * p[j] > M) break;
			isnotp[i * p[j]] = 1;
			if(i % p[j] == 0) break;
		}
	}
}

int Aug(int x) {
	// LOG(x);
	REPG(i, x) if(vis[(v = edge[i].v)] != tim) { 
		vis[v] = tim;
		if(!opn[v] || Aug(opn[v])) {
			opn[x] = v, opn[v] = x;
			return 1;
		}
	}
	return 0;
}

