		n = rd();
		cnt = 0;

		lsz = rsz = 0;
		REP(i, 1, n) a[i] = rd();

		if(a[1] & 1) rt[++ rsz] = a[1];
		else lft[++ lsz] = a[1];
		REP(i, 2, n) {
			if(a[i - 1] != a[i] - 1) {
				if(a[i] & 1) rt[++ rsz] = a[i];
				else lft[++ lsz] = a[i];
			} 
			if(a[i - 1] + 1 != a[i]) {
				if((a[i - 1] + 1) & 1) rt[++ rsz] = a[i - 1] + 1;
				else lft[++ lsz] = a[i - 1] + 1;
			}
		}
		if((a[n] + 1) & 1) rt[++ rsz] = a[n] + 1;
		else lft[++ lsz] = a[n] + 1;

		REP(i, 1, lsz) {
			REP(j, 1, rsz) {
				int x = lft[i], y = rt[j];
				if(isnotp[abs(x - y)]) continue;
				if(abs(x - y) & 1) add(i, lsz + j), add(lsz + j, i);
			}
		}

		fill(vis + 1, vis + lsz + rsz + 1, 0);
		fill(opn + 1, opn + lsz + rsz + 1, 0);
