		REP(i, 1, lsz) if(!opn[i]) {
			++ tim;
			ret += Aug(i);
		}

		int ans = ret;
		ans += ((lsz - ret) / 2 + (rsz - ret) / 2) * 2;
		ans += ((rsz - ret) % 2) * 3;
