#define MAX_N 10000001

vector<int> prime;
bool is_prime[MAX_N];

void sieve(int n){
    for(int i=0;i<=n;i++){
        is_prime[i] = true;
    }
    is_prime[0] = is_prime[1] = false;
    for(int i=2;i<=n;i++){
        if(is_prime[i]){
            prime.push_back(i);
            for(int j=2*i;j<=n;j+=i){
                is_prime[j] = false;
            }
        }
    }
}


int dp[201][201];
bool p[201][201];
vector<int>L,R;
int m;
int dfs(int l, int r){
    if(p[l][r])return dp[l][r];
    p[l][r] = 1;
    if(l==r){
        int s = R[l] - L[l] + 1;
        if(is_prime[s]){
            return dp[l][r] = 1;
        }else if(s%2==0){
            return dp[l][r] = 2;
        }else{
            return dp[l][r] = 3;
        }
    }
    int s = R[r] - L[l] + 1;
    int mi = inf;
    if(is_prime[s]){
        mi = 1 + dfs(l+1,r-1);
    }else if(s%2==0){
        mi = 2 + dfs(l+1,r-1);
    }else{
        mi = 3 + dfs(l+1,r-1);
    }
    for(int k=l;k<r;k+=2){
        mi = min(mi,dfs(l,k) + dfs(k+2,r));
    }
    return dp[l][r] = mi;
}

