template<typename flow_t>
struct FordFulkerson {
  struct edge {
    int to;
    flow_t cap;
    int rev;
    bool isrev;
    int idx;
  };

  vector<vector<edge>> graph;
  vector<int> used;
  const flow_t INF;
  int timestamp;

  FordFulkerson(int n) : INF(numeric_limits<flow_t>::max()), timestamp(0) {
    graph.resize(n);
    used.assign(n, -1);
  }

  void add_edge(int from, int to, flow_t cap, bool undirected = false, int idx = -1) {
    graph[from].emplace_back((edge) {to, cap, (int) graph[to].size(), false, idx});
    graph[to].emplace_back((edge) {from, undirected ? cap : 0, (int) graph[from].size() - 1, true, idx});
  }

  flow_t dfs(int idx, const int t, flow_t flow) {
    if (idx == t) return flow;
    used[idx] = timestamp;
    for (auto &e : graph[idx]) {
      if (e.cap > 0 && used[e.to] != timestamp) {
        flow_t d = dfs(e.to, t, min(flow, e.cap));
        if (d > 0) {
          e.cap -= d;
          graph[e.to][e.rev].cap += d;
          return d;
        }
      }
    }
    return 0;
  }

  flow_t max_flow(int s, int t) {
    flow_t flow = 0;
    for (flow_t f; (f = dfs(s, t, INF)) > 0; ++timestamp) {
      flow += f;
    }
    return flow;
  }
};

vi prime;

bool is_prime(int n) {
  if (n == 1 || !(n & 1)) return false;
  for (int &p : prime) {
    if (p * p > n) break;
    if (n % p == 0) return false;
  }
  return true;
}

void solve() {
  int n; cin >> n;
  vi arr(n); cin >> arr;

  vi sec = {arr[0], arr[0] + 1};
  rep1(i,n) {
    if (sec.back() == arr[i]) {
      ++sec.back();
    } else {
      sec.emplace_back(arr[i]);
      sec.emplace_back(arr[i] + 1);
    }
  }

  vb seen(3200, false);
  for (int i=3; i<3200; i+=2) {
    if (!seen[i]) {
      prime.emplace_back(i);
      for (int j=i*i; j<3200; j+=i*2) {
        seen[j] = true;
      }
    }
  }

  int sz = sec.size();

  FordFulkerson<int> g(sz+2);
  int odd = 0;

  rep(j,sz) {
    bool is_odd = sec[j] & 1;
    if (is_odd) {
      g.add_edge(sz, j, 1);
      ++odd;
    } else {
      g.add_edge(j, sz+1, 1);
    }
    rep(i,j) {
      if (is_prime(sec[j] - sec[i])) {
        if (is_odd) g.add_edge(j, i, 1);
        else g.add_edge(i, j, 1);
      }
    }
  }

  int sum = g.max_flow(sz, sz+1);
  int ans = sz - sum + ((odd - sum) & 1);
  cout << ans << endl;
}


