	for (int i = 0; i < N; i++) {
		if (!mp[X[i] - 1]) V.push_back(X[i]);
		if (!mp[X[i] + 1]) V.push_back(X[i] + 1);
	}
	long long Even = 0, Odd = 0;
	for (int i = 0; i < V.size(); i++) {
		if (V[i] % 2 == 0) add_edge(201, i, 1), Even++;
		else add_edge(i, 202, 1), Odd++;
	}
	for (int i = 0; i < V.size(); i++) {
		if (V[i] % 2 == 1) continue;
		for (int j = 0; j < V.size(); j++) {
			if (V[j] % 2 == 0) continue;
			if (isPrime(abs(V[i] - V[j]))) add_edge(i, j, 1);
		}
	}
