const int maxn = 10000005;
const int maxm = 505;

int prm[maxn/10],tot;bool is[maxn];
int n,x[maxm],a[maxm],m,e[maxm][maxm];
int match[maxm],vis[maxm],ans,odd,even;
void predo(int n) {
	is[0]=is[1]=1;
	for (int i=2;i<=n;i++)
	{
		if (!is[i]) prm[++tot]=i;
		for (int j=1;j<=tot;j++)
		{
			int num=prm[j]*i;
			if (num>n) break;
			if (i%prm[j]) is[num]=1;
			else {is[num]=1;break;}
		}
	}
}
bool Hungray(int u) {
	for (int v=1;v<=m;v++)
	if (e[u][v]&&!vis[v]) {
		vis[v]=true;
		if (!match[v]||Hungray(match[v])) {
			match[v]=u;return true;
		}
	}
	return false;
}
int main()
{
	#ifdef Amberframe
		freopen("arc080f.in","r",stdin);
		freopen("arc080f.out","w",stdout);
	#endif
	predo(1e7);scanf("%d",&n);x[0]=-1;
	for (int i=1;i<=n;i++) scanf("%d",&x[i]);
	for (int i=1;i<=n;i++) x[i]!=x[i-1]+1?a[++m]=x[i]:0;
	for (int i=1;i<=n;i++) x[i]!=x[i+1]-1?a[++m]=x[i]+1:0;
	sort(a+1,a+m+1);is[2]=1;
	for (int i=1;i<=m;i++)
	for (int j=i+1;j<=m;j++)
		if (!is[a[j]-a[i]]) e[i][j]=e[j][i]=1;
	for (int i=1;i<=m;i++)
	if (a[i]&1) {
		++odd;memset(vis,0,sizeof vis);
		if (Hungray(i)) ++ans;
	}
	else ++even;
	odd-=ans;even-=ans;
	ans+=2*(odd/2+even/2)+(odd&1)*3;
	printf("%d",ans);
	return 0;
}

#include <bits/stdc++.h>
using namespace std;
struct hopcroft{
	vector<int> dist, pair_l, pair_r;
	vector<bool> seen;
	vector<vector<int>> to;
	int n1, n2;
	hopcroft(int n1, int n2) : n1(n1), n2(n2){
		to.resize(n1);
		pair_l.assign(n1, -1);
		pair_r.assign(n2, -1);
		dist.resize(n1);
		seen.resize(n1);
	}
	void add_edge(int u, int v){
		to[u].push_back(v);
	}
	bool bfs(){
		fill(dist.begin(), dist.end(), -1);
		queue<int> q;
		for (int i = 0; i < n1; i++){
			if (pair_l[i] == -1){
				dist[i] = 0;
				q.push(i);
			}
		}
		bool reach_minus = false;
		while (!q.empty()){
			int u = q.front();
			q.pop();
			for (int adj : to[u]){
				int radj = pair_r[adj];
				if (radj == -1){
					reach_minus = true;
				} else if (dist[radj] == -1){
					dist[radj] = dist[u] + 1;
					q.push(radj);
				}
			}
		}
		return reach_minus;
	}
	bool dfs(int u){
		seen[u] = true;
		for (int adj : to[u]){
			int radj = pair_r[adj];
			if (radj == -1 || (!seen[radj] && dist[radj] == dist[u] + 1 && dfs(radj))){
				pair_l[u] = adj;
				pair_r[adj] = u;
				return true;
			}
		}
		return false;
	}
	int max_matching(){
		int res = 0;
		while (bfs()){
			fill(seen.begin(), seen.end(), false);
			for (int i = 0; i < n1; i++)
				if (pair_l[i] == -1 && dfs(i))
					res++;
		}
		return res;
	}
};
const int lim = 10000002;
bool prime[lim];
void init(){
	memset(prime, true, sizeof(prime));
	prime[0] = prime[1] = false;
	for(int i = 2; i < lim; i++){
		if(prime[i]){
			for(int j = i + i; j < lim; j += i)
				prime[j] = false;
		}	
	}
}
int main(){
	init();
	int n; cin >> n;
	bitset<lim> b;
	for(int i = 0; i < n; i++){
		int x; cin >> x;
		b[x] = 1;
	}
	vector<int> p[2];
	for(int i = 1; i < lim; i++){
		if(b[i] != b[i - 1]){
			p[i%2].push_back(i);
		}
	}
	int l0 = p[0].size(), l1 = p[1].size();
	hopcroft ho(l0, l1);
	assert((l0 + l1) % 2 == 0);
	for(int i = 0; i < l0; i++){
		for(int j = 0; j < l1; j++){
			if(prime[abs(p[0][i] - p[1][j])]){
				ho.add_edge(i, j);
			}
		}
	}
	int fl = ho.max_matching();
	int ans = fl + ((l0 - fl) / 2) * 2 + ((l1 - fl)/2) * 2 + ((((l0 - fl) % 2) + ((l1 - fl) % 2))/2) * 3;
	cout << ans << endl;
	return 0;
}

